import React from "react";
import { useState, useEffect } from "react";
import { usePlacesList } from "../placesContext";
import ScrollReveal from "scrollreveal";

import '../styles/Map.css'
import SideCard from "../components/sideCard";




export default function Map() {
  
  const places = usePlacesList()
  
   const [state, setState] = useState(null)

   useEffect(()=>{
       ScrollReveal().reveal('.pinpoint', {delay: 300,duration: 1000,distance: '50px',origin: 'bottom',easing: 'ease-in-out',reset: true
    });
     ScrollReveal().reveal('.side-card', {delay: 500,duration: 1200,distance: '60px',origin: 'right',easing: 'ease-in-out',reset: true  
    });
   })
  
  return(
  <>
   <section id="mapa">
   <div className="mapSection">
      <div className="map">
        <img src="assets/mapasempontos.png" class="map" alt=""/>

         
         {places.map((place) => (
            <div key={place.id} className={`${place.className} pinpoint`} onClick={() => {setState(place.id)}} >
              <h1>{place.name}</h1>
              <img src="assets/pin verde.png"  alt=""/>
            </div>



         ))
         
         } 
         
         
      </div>
     


      <SideCard places={places} Id={state}/>
      

    </div>
    </section>
  
  </>
  
  )


}
