import React from "react";
import { useRef } from "react";

export default function AddCard() {

  const nameRef = useRef()
  const descriptionRef = useRef()



    return (
      <div className="form-container">
    <h2>Adicionar Novo Pin</h2>
    <form>
      <label for="nome">Nome do Pin</label>
      <input type="text" id="nome" name="nome" placeholder="Ex:Pin de Localização" required />

      <label for="descricao">Descrição</label>
      <textarea id="descricao" name="descricao" placeholder="Descreva seu pin..." rows="2" required></textarea>

      <label for="imagem">Imagem Ilustrativa</label>
      <input type="file" id="imagem" name="imagem" accept=".svg,.png,.jpg,.jpeg" />

      <div id="preview-container">
        <p>Pré-visualização do seu pin</p>
        <div id="img-preview"></div>
      </div>

      <button type="submit">Salvar Pin</button>
    </form>
  </div>
    )
}
