import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

export default function Chart({ id, dataKey, date }) {

 

  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [{
      data: [],
    }]
  });

 const fetchData = async () => {
  const response = await fetch(`http://localhost:3000/api/medias${date}/${id}?timestamp=${Date.now()}`);
  const dataMedia = await response.json();

  if (!Array.isArray(dataMedia) || dataMedia.length === 0) {
    console.error('API retornou vazio ou inválido:', dataMedia);
    return;
  }

  const novasLabels = dataMedia.map(d =>
    d.timestamp
      ? new Date(d.timestamp).toLocaleDateString('pt-BR', {
          day: 'numeric',
          month: 'short'
        }).replace(' de ', ' ')
      : 'Data inválida'
  );

  const novosDados = dataMedia.map(d =>
    d[dataKey] !== undefined ? parseFloat(d[dataKey]) : null
  );

  setChartData({
    labels: novasLabels,
    datasets: [{
      data: novosDados
    }]
  });
};


  useEffect(() => {
    fetchData(); 

    const interval = setInterval(() => {
      fetchData();
    }, 3000);

    return () => clearInterval(interval); 
  }, []);

  return (
    <>
      <Line
        data={chartData}
        options={{
          responsive: true,
     maintainAspectRatio: false,
          scales: {
            y: {
              ticks: {
                callback: function(value) {
                  return value + '°C';
                }
              }
            },
            x: { display: true }
          },
          plugins: {
            legend: { display: false },
            tooltip: {
              callbacks: {
                label: function(context) {
                  return context.raw + '°C';
                }
              }
            }
          }
        }}
      />
    </>
  );
}
