import React, { useState, useRef } from "react";
import { usePlacesList, useUpdatePlaces } from "../placesContext"; // ajuste conforme seu projeto
import TutorialModal from "./TutorialModal";

export default function AddCard() {
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);
  const [modal, setModal] = useState(false);
  const [selecting, setSelecting] = useState(false);
  const [positionPX, setPositionPX] = useState({ top: "0", left: "0" });

  const nameRef = useRef();
  const descRef = useRef();
  const imgRef = useRef();

  const refreshPlaces = useUpdatePlaces();
  const places = usePlacesList();
  const place = places[places.length - 1];

  // Ativa modo de seleção
  function handlePosition() {
    setSelecting(true);
    document.body.style.cursor = "crosshair";
  }

  // Captura clique no mapa
  function getPosition(e) {
    if (!selecting) return;

    const rect = e.target.getBoundingClientRect();
    const left = ((e.clientX - rect.left) / rect.width) * 100;
    const top = ((e.clientY - rect.top) / rect.height) * 100;

    setPositionPX({ top: `${top}vh`, left: `${left}vw` });
    setSelecting(false);
    document.body.style.cursor = "default";
  }

  async function handleSubmit(e) {
    e.preventDefault();

    const name = nameRef.current.value;
    const desc = descRef.current.value;
    const img = imgRef.current.files[0];

    const lastId = Number(places[places.length - 1]?.id || 0);
    const id = lastId + 1;

    if (!name || !desc || !img || !positionPX.top || !positionPX.left) {
      setError(true);
      return;
    }

    const positionJSON = {
      top: positionPX.top,
      left: positionPX.left,
    };

    const formData = new FormData();
    formData.append("id", id.toString());
    formData.append("name", name);
    formData.append("desc", desc);
    formData.append("position", JSON.stringify(positionJSON));
    formData.append("img", img);

    await fetch("https://back-pure-space.vercel.app/send/places", {
      method: "POST",
      body: formData,
    });

    setSuccess(true);
    refreshPlaces();
  }

  if (success) {
    return (
      <div className="success">
        <h1>Sensor adicionado com sucesso!</h1>
        <TutorialModal open={modal} fecharModal={() => setModal(false)} />
        <button onClick={() => setModal(true)}>Guia de Instalação</button>
      </div>
    );
  }

  return (
    <div className="form-container">
      <h2>Adicionar Novo Pin</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="nome">Nome do Pin</label>
        <input
          type="text"
          ref={nameRef}
          id="nome"
          placeholder="Ex: Pin de Localização"
          required
        />

        <label htmlFor="descricao">Descrição</label>
        <textarea
          ref={descRef}
          placeholder="Descreva seu pin..."
          rows="2"
          required
        />

        <label htmlFor="imagem">Imagem Ilustrativa</label>
        <input type="file" ref={imgRef} accept=".svg,.png,.jpg,.jpeg" required />

        <p className="error-msg" style={{ display: error ? "block" : "none" }}>
          Preencha todos os campos para continuar<span className="dot">.</span>
        </p>

        <button type="button" onClick={handlePosition}>
          Escolha posição do Pin
        </button>
        <p
          className="selectPlace-msg"
          style={{ display: selecting ? "block" : "none" }}
        >
          Clique em algum lugar no mapa para posicionar
        </p>

        <div className="map-wrapper">
          <img
            src="assets/mapasempontos.png"
            className="map"
            alt=""
            onClick={getPosition}
          />
        </div>

        <button type="submit">Salvar Pin</button>
      </form>
    </div>
  );
}
