import React from "react";
import { useRef, useState, useEffect } from "react";
import '../styles/addPinPage.css'
import { usePlacesList } from "../placesContext.js";

export default function EditCard({ sensorId, positionState }) {

  const nameRef = useRef()
  const descRef = useRef()
  const imgRef = useRef()
  const [state,setState] = useState(false)
  const [positionPX, setStatePX] = useState({
    top: '0',
    left: '0'
  })
  const places = usePlacesList()
  const placeInfo = places.find((place) => place.id === sensorId);

 

 async function handleSubmit(e) {
     e.preventDefault();

    const name = nameRef.current.value;
    const desc = descRef.current.value;
    const img = imgRef.current.files[0]; 
      
    
    const lastId = Number(places[places.length-1].id)
    const id = lastId+1
    
   const positionJSON = {}

    positionJSON.left = `${positionPX.left}vw`
    positionJSON.top = `${positionPX.top}vh`
    
   

    const formData = new FormData();
    formData.append('id', id.toString());
    formData.append('name', name);
    formData.append('desc', desc);
    formData.append('position', JSON.stringify(positionJSON));
    formData.append('img', img);
    
    if (!name || !desc || !positionJSON) {
      console.log("um dos campos está vazio")
      return
    }
 
    
 
    await fetch('http://localhost:3000/send/places', {
        method: 'POST',
        body: formData
  })
  
 }

    function handlePosition() {
     setState(true)
     
      document.body.style.cursor = 'crosshair';
    }

     useEffect(() => {
          
       if(!state)  {
      return  
     }
      else {
       setState(false)
       setStatePX({ ...positionState });
       document.body.style.cursor = 'default'
      
      } 
     },[positionState])    
     


    return (
      <div className="form-container">
    <h2>Atualizar Pin</h2>
    <form onSubmit={handleSubmit}>
      <label for="nome">Nome do Pin</label>
      <input type="text" ref={nameRef} id="nome" name="nome" placeholder="Ex:Pin de Localização" value={placeInfo.name} required />

      <label for="descricao">Descrição</label>
      <textarea id="descricao" ref={descRef} name="descricao" placeholder="Descreva seu pin..." value={placeInfo.desc} rows="2" required></textarea>

      <label for="imagem">Imagem Ilustrativa</label>
      <input type="file" ref={imgRef} id="imagem" name="imagem" accept=".svg,.png,.jpg,.jpeg" />

      <div id="preview-container">
        <p>Pré-visualização do seu pin</p>
        <div id="img-preview"></div>
      </div>
      <button type="button" onClick={handlePosition}>Atualizar posição do Pin</button>
      <button type="submit">Atualizar Pin</button>
    </form>
  </div>
    )
}
