import ScrollReveal from 'scrollreveal';

const sr = ScrollReveal({ reset: true });

export function initScrollReveal() {
  sr.reveal('.avaliation-card', {
    duration: 2000,
    rotate: { x: 0, y: 100, z: 0 },
  });

  sr.reveal('.card', {
    duration: 1000,
    rotate: { x: 0, y: 100, z: 0 },
  });

  sr.reveal('.avaliacao', {
    duration: 1000,
    distance: '100px',
    origin: 'top',
  });

  sr.reveal('.map img', {
    duration: 1000,
    scale: 0.8,
    rotate: { x: 0, y: 100, z: 0 },
  });

  sr.reveal('.pinpoint', {
    duration: 1000,
    distance: '50px',
    origin: 'bottom',
    interval: 100
  });

  sr.reveal('.side-card', {
    duration: 1200,
    distance: '60px',
    origin: 'right',
    easing: 'ease-in-out',
  });
}

export function cleanScrollReveal() {
  sr.clean('.avaliation-card, .card, .avaliacao, .map img, .pinpoint, .side-card');
}
