import React, { useEffect, useState } from "react";
import { avaliacao_Atual } from "../util/avaliacao";
import AddCard from "./AddPin";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


export default function SideCard({ places, Id, position }) {
  const [data, setData] = useState(null);
  const [animateClass, setAnimateClass] = useState('animation');
  

  useEffect(() => {
    if (Id || Id == '0') {
      const fetchSensorData = async () => {
        const response = await fetch(`http://localhost:3000/api/atual/${Id}`);
        const data = await response.json();
        setData(data[0]);
      };

      fetchSensorData();

      
      setAnimateClass('');
      
      setTimeout(() => {
        setAnimateClass('animation');
      }, 10);  
    }
  }, [Id]);

  if (Id === null || Id === undefined) {
    return (
      <div className="control">
        <img className="svg1" src="assets/undraw_world_bdnk.svg" alt="" />
        <p>escolha algum ponto para come√ßar.</p>
      </div>
    );
  }

  if(Id === '-2') {
    console.log('-2')
    return (<AddCard positionState={position}/>)
  }

  const placeInfo = places.find((place) => place.id === Id);


  if (!data) {
    return <p>Carregando dados do sensor...</p>;
  }

  const { avaliationTemp, avaliationHum } = avaliacao_Atual(data.temp, data.hum);
  console.log(Id)
  return (
    <>
       <div className={`infos ${animateClass}`}>
        <FontAwesomeIcon icon={'fa-solid'} />
        <img src={placeInfo.img} alt="" />
        <h1>{placeInfo.name}</h1>
        <h2 className="infos-temp">Temperatura atual: {data.temp}¬∞C üå°Ô∏è</h2>
        <p className="avaliacao-temp-atual">avalia√ß√£o: {avaliationTemp}</p>
        <h2 className="infos-hum">Umidade atual do ar: {data.hum}% üíß</h2>
        <p className="avaliacao-hum-atual">avalia√ß√£o: {avaliationHum}</p>
        <button className="moreInfos">Informa√ß√µes mais detalhadas</button>
      </div>
    </>
  );
}
