import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

export default function MonthChart({ id, dataKey }) {

 

  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [{
      data: [],
    }]
  });

  // Função para buscar dados da API
  const fetchData = async () => {
    const response = await fetch(`http://localhost:3000/api/mediasmonth/${id}?timestamp=${Date.now()}`);
    const dataMedia = await response.json();

    const novasLabels = dataMedia.map(d =>
      new Date(d.timestamp).toLocaleDateString('pt-BR', {
        day: 'numeric',
        month: 'short'
      }).replace(' de ', ' ')
    );

    const novosDados = dataMedia.map(d => parseFloat(d[dataKey]));


    setChartData({
      labels: novasLabels,
      datasets: [{
        data: novosDados
      }]
    });
  };


  useEffect(() => {
    fetchData(); 

    const interval = setInterval(() => {
      fetchData();
    }, 3000);

    return () => clearInterval(interval); 
  }, []);

  return (
    <>
      <Line
        data={chartData}
        options={{
          responsive: true,
          scales: {
            y: {
              ticks: {
                callback: function(value) {
                  return value + '°C';
                }
              }
            },
            x: { display: true }
          },
          plugins: {
            legend: { display: false },
            tooltip: {
              callbacks: {
                label: function(context) {
                  return context.raw + '°C';
                }
              }
            }
          }
        }}
      />
    </>
  );
}
