import React from "react";
import '../styles/addPinPage.css'


export default function AddPinPage() {

   const imagemInput = document.getElementById('imagem');
    const imgPreview = document.getElementById('img-preview');

    imagemInput.addEventListener('change', () => {
      const file = imagemInput.files[0];
      if (file && file.type.startsWith("image/")) {
        const reader = new FileReader();
        reader.onload = function(e) {
          imgPreview.innerHTML = `<img src="${e.target.result}" alt="Prévia da imagem" />`;
        };
        reader.readAsDataURL(file);
      } else {
        imgPreview.innerHTML = "<p style='color:red;'>Arquivo inválido. Selecione uma imagem.</p>";
      }
    });


    return(<>
    <div className="form-container">
    <h2>Adicionar Novo Pin</h2>
    <form>
      <label for="nome">Nome do Pin</label>
      <input type="text" id="nome" name="nome" placeholder="Ex:Pin de Localização" required />

      <label for="descricao">Descrição</label>
      <textarea id="descricao" name="descricao" placeholder="Descreva seu pin..." rows="3" required></textarea>

      <label for="imagem">Imagem Ilustrativa</label>
      <input type="file" id="imagem" name="imagem" accept=".svg,.png,.jpg,.jpeg" />

      <div id="preview-container">
        <p>Pré-visualização do seu pin</p>
        <div id="img-preview"></div>
      </div>

      <button type="submit">Salvar Pin</button>
    </form>
  </div>
    
    </>)
}
